import React, { useState, useRef } from "react";
import "../App.css";

function TodoForm({ createTodo }) {
  const [newTodoValue, setNewTodoValue] = useState("");

  const updateInputRef = useRef(null)

 const onEditButtonClick = () => {
    // updateInputRef.current.display = 'block'
  }



  const handleCreateButtonClick = (e) => {
      e.preventDefault();
      createTodo(newTodoValue)
      setNewTodoValue('')
  }

  return (
    <>
      <form className="todo-form" >
        <input
          type="text"
          placeholder="What to do?"
          className="todo-input"
          onChange={event => setNewTodoValue(event.target.value)}
          value={newTodoValue}
        />
        <button className="todo-button" onClick={handleCreateButtonClick}>
          ok
        </button>
        <input 
        type ='text'
        placeholder = 'Update your todo'
        display="none"
        className = 'todo-input edit'
        ref={updateInputRef}
        /> 
        <button 
        onClick={onEditButtonClick}>Update</button>

      </form>
    </>
  );
}

export default TodoForm;
