import React, { useState, useRef } from "react";

function Todo({ todo, updateTodo, removeTodo  }) {
  const displayMode = "displayMode";
  const editMode = "editMode";

  const [mode, setMode] = useState(displayMode);

  const [newTodoText, setNewTodoText] = useState(todo.text)

  const updateInputRef = useRef();




  const onEditButtonClick = (e) => {
    e.preventDefault();
    // updateInputRef.current.value=todo.text
    setMode(editMode);
  };

  const onSubmitButtonClick = (e) => {
    e.preventDefault();
    updateTodo(todo.id, newTodoText)
    setMode(displayMode)
  }


  if (mode === displayMode) {
    return (
      <div>
        <p>
          {todo.id} {todo.text}
        </p>
        <button className="button-edit" onClick={onEditButtonClick}>
          Update
        </button>
        <button onClick={() => removeTodo(todo.id)}>Delete</button>
      </div>
    );
  } else if (mode === editMode) {
    return (
      <div>
        <input
          type="text"
          placeholder="Update your todo"
          className="input-edit"
          onChange={(event) => setNewTodoText(event.target.value)}
          // ref={updateInputRef}
          value={newTodoText}
        />
        <button className="button-edit" onClick={onSubmitButtonClick}>Submit</button>
      </div>
    );
  }
}

export default Todo;
