import React, {useState, useEffect} from "react";

function ButtonText() {

    const [catName, setCatName] = useState('Tom');
    const [catSex, setCatSex] = useState('Boy');

    useEffect(() => {
        if(catName === 'Tom'){
            setCatName()
        }
    }, [catName])

  return (
    <>
      <p>Privet {catName} - {catSex}</p>
      <button onClick={() => setCatName("Var'ka")}>Var'ka</button>
      <button onClick={() => setCatName("Herz")}>Herz</button>
    </>
  );
}

export default ButtonText
