import React, {useState} from "react";
import Todo from './components/Todo'
import "./App.css";

function App() {

  const defaultTodo = [
    {
      id: 1,
      text: "walk with a dog",
    },
    {
      id: 2,
      text: "buy bread",
    },
  ];

  const [todos, setTodos] = useState(defaultTodo);

  const todoHtml = todos.map((todo) => (
    <Todo todo={todo} />
  ))



  return <div className="todo-app">
    {todoHtml}
  </div>;
}

export default App;
