import React, { useState } from "react";
import Todo from "./components/Todo";
import "./App.css";

function App() {
  const defaultTodo = [
    {
      id: 1,
      text: "walk with a dog",
    },
    {
      id: 2,
      text: "buy bread",
    },
  ];

  const [todos, setTodos] = useState(defaultTodo);
  const [newTodoValue, setNewTodoValue] = useState('');




  const handleCreateInputChange = (e) => {
    setNewTodoValue(e.target.value);
  };


  const createTodo = (e) => {
    e.preventDefault();
    if(newTodoValue === '') {
      return 
    }

    const newTodo = {
      id: Math.floor(Math.random() * 10000),
      text: newTodoValue
    }

    const newTodos = [
      ...todos, newTodo
    ]

    setTodos(newTodos)
    setNewTodoValue('')

  }

  const removeTodo = (id) => {
    const newTodos = todos.filter(todo => todo.id !== id)
    setTodos(newTodos)
    console.log('removing to do with id - ' + id)
  }

  const todoHtml = todos.map((todo) => <Todo todo={todo} removeTodo={removeTodo} />);

  return <div className="todo-app">
    <form className='todo-form' onSubmit={createTodo}>
      <input
      type='text'
      placeholder='What to do?'
      className='todo-input'
      onChange={handleCreateInputChange}
      value={newTodoValue}       
      />
      <button type='submit' className='todo-button'>ok</button>
    </form>

    {todoHtml}
    </div>;
}

export default App;
