import React, {useRef} from "react";

function Todo({ todo, removeTodo }) {

  const updateInputRef = useRef();

  const onEditButtonClick = (e) => {
    e.preventDefault();
    updateInputRef.current.style.display = "block";
    updateInputRef.current.focus()
  };

  return (
    <div>
      <p>
        {todo.id} {todo.text}
      </p>
      <button onClick={() => removeTodo(todo.id)}>Delete</button>
      <div>
        <form className='todo-form'>
          <input
              type="text"
              placeholder="Update your todo"
              className="input-edit"
              ref={updateInputRef}
            />
            <button className="button-edit" onClick={onEditButtonClick}>
              Update
            </button>
        </form>
      </div>
      <div>
      <form className='todo-form'>
          <input
              type="text"
              placeholder=""
              className="input-edit"
              ref={updateInputRef}
            />
            <button className="button-edit" onClick={onEditButtonClick}>
              Submit
            </button>
        </form>
      </div>
    </div>
  );
}

export default Todo;
