import React, { useState, useRef } from "react";
import "../App.css";

function TodoForm({ createTodo }) {
  const [newTodoValue, setNewTodoValue] = useState("");

  const updateInputRef = useRef();

  const onEditButtonClick = (e) => {
    e.preventDefault();
    updateInputRef.current.style.display = "block";
    updateInputRef.current.focus()
  };

  const handleCreateButtonClick = (e) => {
    e.preventDefault();
    createTodo(newTodoValue);
    setNewTodoValue("");
  };

  return (
    <>
      <form className="todo-form">
        <input
          type="text"
          placeholder="What to do?"
          className="todo-input"
          onChange={(event) => setNewTodoValue(event.target.value)}
          value={newTodoValue}
        />
        <button className="todo-button" onClick={handleCreateButtonClick}>
          ok
        </button>
        <input
          type="text"
          placeholder="Update your todo"
          className="input-edit"
          ref={updateInputRef}
        />
        <button className="button-edit" onClick={onEditButtonClick}>
          Update
        </button>
      </form>
    </>
  );
}

export default TodoForm;
