import React, { useState } from "react";
import { AiOutlineEdit } from "react-icons/ai";
import { AiOutlineCloseCircle } from "react-icons/ai";
import { FaCheck } from "react-icons/fa";
import styled from 'styled-components'

function Todo({ todo, updateTodo, removeTodo, isHighlighted, completeTodo, state }) {
  const displayMode = "displayMode";
  const editMode = "editMode";

  const [mode, setMode] = useState(displayMode);

  const [newTodoText, setNewTodoText] = useState(todo.text);

  const [checked, setChecked] = useState(true)





  const onEditButtonClick = (e) => {
    e.preventDefault();
    setMode(editMode);
  };

  const onSubmitButtonClick = (e) => {
    e.preventDefault();
    const newTodo = todo;
    newTodo.text = newTodoText;

    updateTodo(todo.id, newTodo);
    setMode(displayMode);
  };

  if (mode === displayMode) {
    return (
      <>
        <div className={`todo-item  ${isHighlighted ? "highlightedTodo" : ""}`}>
        <input 
        className='checkbox' 
        type='checkboc'
        onClick={() => completeTodo(todo.id)} 
        checked={checked}
        onChange={() => setChecked(!checked)}

        />
          <div className={`todo-text ${todo.isCompleted && 'todo-is-completed'}`}>
            {todo.text}                       
          </div>
          <div className="icon-edit">
            <AiOutlineEdit onClick={onEditButtonClick} />
          </div>
          <div className="icon-delete">
            <AiOutlineCloseCircle onClick={() => removeTodo(todo.id)} />
          </div>
        </div>
      </>
    );
  } else if (mode === editMode) {
    return (
      <>
        <form className="submit-todo-form">
          <input
            type="text"
            placeholder="Update your todo"
            className="submit-todo-input"
            onChange={(event) => setNewTodoText(event.target.value)}
            value={newTodoText}
          />
          <button className="button-submit" onClick={onSubmitButtonClick}>
            <FaCheck />
          </button>
        </form>
      </>
    );
  }
}

export default Todo;
