import React, {useState} from 'react'
import './App.css';

function App() {

  const defaultTodo = [
    {
      id: 1,
      text: 'walk with a dog'
    },
    {
      id: 2,
      text: 'buy bread'
    }
  ]




 const [todos, setTodos] = useState(defaultTodo)

const todosHtml = todos.map((el) => (<p>{el.id} {el.text}</p>))



  return (
    <div className="todo-app">
      {todosHtml}     
    </div>
  );
}

export default App;
